package guanabaraAulas;

public class EstruturasRepeticao1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// WHILE (ENQUANTO)

		int c = 1;

		while (c <= 4) {

			System.out.println("Cambalhota " + c); //

			c++; // cc = cc +1; || cc += 1;
		}

		/*
		 * MUDANDO O FLUXO DO LAÇO:
		 * 
		 * A QUALQUER MOMENTO EU POSSO USAR UMA CONDICIONAL E INSERIR O COMANDO
		 * "CONTINUE;" OU "BREAK;" DENTRO DO LAÇO.
		 * 
		 * EX:
		 * 
		 * IF (c == 2) {
		 * 
		 * continue; // O CONTINUE FAZ COM QUE A CAMBALHOTA NÃO SEJA EXIBIDA QUANDO FOR
		 * IGUAL AO NÚMERO ESPECIFICADO E RETORNA AO INÍCIO DO LOOP PARA DAR
		 * CONTINUIDADE;
		 * 
		 * }
		 * 
		 * 
		 * * IF (c == 3) {
		 * 
		 * break; // O BREAK FAZ COM QUE O LOOP SEJA ENCERRADO AO CHEGAR NA CONTAGEM
		 * ESPECIFICADA.
		 * 
		 * }
		 * 
		 * 
		 */

	}

}
